mapper.update:
update orders
orderNamech=#{orderNamech,jdbcType=VARCHAR}, orderNameen=#{orderNameen,jdbcType=VARCHAR}, summary=#{summary,jdbcType=VARCHAR}, businessid=#{businessid,jdbcType=VARCHAR}, publishtime=#{publishtime,jdbcType=VARCHAR}, img1=#{img1,jdbcType=VARCHAR}, img2=#{img2,jdbcType=VARCHAR}, img3=#{img3,jdbcType=VARCHAR}, clickcount=#{clickcount,jdbcType=VARCHAR}, goodsname=#{goodsname,jdbcType=VARCHAR}, number=#{number,jdbcType=VARCHAR}, categoryid=#{categoryid,jdbcType=VARCHAR}, specifications=#{specifications,jdbcType=VARCHAR}, price=#{price,jdbcType=VARCHAR}, supplytime=#{supplytime,jdbcType=VARCHAR}, comid=#{comid,jdbcType=VARCHAR}, details=#{details,jdbcType=VARCHAR}, recomflag=#{recomflag,jdbcType=VARCHAR}, publishflag=#{publishflag,jdbcType=VARCHAR}, tradeflag=#{tradeflag,jdbcType=VARCHAR}, supplyflag=#{supplyflag,jdbcType=VARCHAR}, delflag=#{delflag,jdbcType=VARCHAR}, comname=#{comname,jdbcType=VARCHAR}, category=#{category,jdbcType=VARCHAR}, updatetime=now()
where orderid=#{orderid,jdbcType=VARCHAR}

mapper.select:
      select * from orders where delflag = 0 and supplyflag=#{supplyflag} 
     
      	
       		and publishflag = #{publishflag}
		 
		
       	and( orderNamech like '%${orderNamech}%' or orderNameen like '%${orderNamech}%' )
		
		
       	and comname like '%${comname}%'
		 
		
       	and businessid = #{businessid}
		 
		
       	and publishtime >=  #{publishtime}
		        
       order by updatetime desc
